function showCategory(category) {
    const container = document.getElementById('shayari-list');
    container.innerHTML = ''; // Clear old content

    shayariData[category].forEach((text, index) => {
        const div = document.createElement('div');
        div.className = 'shayari';
        div.innerHTML = `
      <p>${text}</p>
      <button class="copy-btn" onclick="copyShayari('${text}')">ЁЯУЛ рдХреЙрдкреА рдХрд░реЗрдВ</button>
      <button class="share-btn" onclick="shareShayari('${text}')">ЁЯУд рд╢реЗрдпрд░ рдХрд░реЗрдВ</button>
    `;
        container.appendChild(div);
    });
}

